import pytestfrom app.trivia import Question# Respuesta correctadef test_question_correct_answer():    question = Question("¿Qué juego ganó el GOTY el año 2024?", ["Astro Bot", "God of War Ragnarok", "The Last of Us 2", "Ghost of Tsushima"], "1")    assert question.is_correct("1")# Respuesta incorrectadef test_question_incorrect_answer():    question = Question("¿Qué juego ganó el GOTY el año 2024?", ["Astro Bot", "God of War Ragnarok", "The Last of Us 2", "Ghost of Tsushima"], "1")    assert not question.is_correct("2")# Respuesta invalidadef test_question_invalid_answer():    question = Question("¿Qué juego ganó el GOTY el año 2024?", ["Astro Bot", "God of War Ragnarok", "The Last of Us 2", "Ghost of Tsushima"], "1")    assert not question.is_correct("5")# Respuesta no permitidadef test_question_incorrect_type_input():    question = Question("¿Qué juego ganó el GOTY el año 2024?", ["Astro Bot", "God of War Ragnarok", "The Last of Us 2", "Ghost of Tsushima"], "1")    assert not question.is_correct(2)# Respuesta vacíadef test_question_empty_answer():    question = Question("¿Qué juego ganó el GOTY el año 2024?", ["Astro Bot", "God of War Ragnarok", "The Last of Us 2", "Ghost of Tsushima"], "1")    assert not question.is_correct("")